{
    "name": "My First Workflow",
    "description": "A sample workflow to get started",
    "nodes": [
        {
            "id": "trigger_1",
            "type": "trigger",
            "name": "Manual Trigger",
            "config": {},
            "position": {"x": 100, "y": 100}
        },
        {
            "id": "action_1",
            "type": "action",
            "name": "HTTP Request",
            "config": {
                "url": "https://api.example.com/data",
                "method": "GET"
            },
            "position": {"x": 300, "y": 100}
        }
    ],
    "connections": [
        {
            "id": "conn_1",
            "sourceNodeId": "trigger_1",
            "targetNodeId": "action_1"
        }
    ],
    "settings": {
        "maxExecutionTime": 300,
        "errorHandling": "stop"
    },
    "tags": ["sample", "demo"]
}

{
    "name": "My Custom Node",
    "description": "A custom node for processing data",
    "category": "transform",
    "icon": "code",
    "code": "module.exports = async function(input) { return { result: input.data * 2 }; }",
    "runtime": "javascript",
    "inputs": [
        {
            "name": "data",
            "type": "number",
            "required": true,
            "description": "Input number to process"
        }
    ],
    "outputs": [
        {
            "name": "result",
            "type": "number",
            "description": "Processed result"
        }
    ],
    "isPublic": false
}

